import React from 'react';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import SocialButtons from './SocialButtons';
import Typewriter from 'typewriter-effect';
import { Container } from '@material-ui/core';


const useStyles = makeStyles((theme)=>({
  root: {
    width: '100%',
    backgroundImage: `url(${"/assets/bg1.png"})`,
    backgroundSize:'cover',
    padding:24,
    height:'100vh',
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    [theme.breakpoints.down('xs')]: {
        padding:18
       },
  },
  mainHead:{
      fontWeight:600,
      marginBottom:0,
      color:'white',
    [theme.breakpoints.down('xs')]: {
       fontSize:'3.8rem'
      },
      [theme.breakpoints.up('sm')]: {
        fontSize:'4.2rem'
       },
       [theme.breakpoints.up('lg')]: {
        fontSize:'6rem'
       }
  },
  subHead:{
    color:'white',
    [theme.breakpoints.down('xs')]: {
        fontSize:'1.6rem'
       },
       [theme.breakpoints.up('sm')]: {
         fontSize:'2.125rem'
        },
        [theme.breakpoints.up('lg')]: {
         fontSize:'2.125rem'
        }
  },
  lowerHead:{
    color:'white',
    fontWeight:'bold'
  }
}));

export default function About() {
  const classes = useStyles();

  return (
    
    <div className={classes.root}>
    <Container  style={{border:'1px solid red',height:'100vh',display:'flex'}}>
      <Typography variant="h1" component="h2" gutterBottom className={classes.mainHead}>
      <span style={{color:'#00796b'}}> BASIT </span> MIR
      </Typography>
     
      <Typography variant="h4" gutterBottom className={classes.subHead}>
      {/* <span style={{color:'#00796b'}}> I'm </span> */}
      <Typewriter
           
            options={{
              strings: ['Software Engineer', 'Application Developer','Programmer'],
              autoStart: true,
              loop: true,
              
            }}
        />
       
      </Typography>
     
      <Typography variant="body1" gutterBottom className={classes.lowerHead}>
        If you can imagine it, I can make it...
      </Typography>
      </Container>
      <div>
          <SocialButtons/>
      </div>
     
    </div>
    
  );
}
